/** @format */

import axios from "axios";
import React, { useEffect, useState } from "react";
import Heading from "../../components/Heading";
import toast from "react-hot-toast";
import { BiArrowBack } from "react-icons/bi";
import { baseApiURL } from "../../baseUrl";

const Marks = () => {
	const [subject, setSubject] = useState();
	// const [branch, setBranch] = useState();
	const [section, setSection] = useState();
	const [studentData, setStudentData] = useState();
	const [selected, setSelected] = useState({
		semester: "",
		section: "",
		subject: "",
		examType: "",
	});
	const loadStudentDetails = () => {
		const headers = {
			"Content-Type": "application/json",
		};
		axios
			.post(
				`${baseApiURL()}/student/details/getDetails`,
				{
					section: selected.section,
					semester: selected.semester,
				},
				{ headers }
			)
			.then((response) => {
				if (response.data.success) {
					setStudentData(response.data.user);
				} else {
					toast.error(response.data.message);
				}
			})
			.catch((error) => {
				console.error(error);
				toast.error(error.message);
			});
	};

	const submitMarksHandler = () => {
		let container = document.getElementById("markContainer");
		container.childNodes.forEach((enroll) => {
			setStudentMarksHandler(
				enroll.id,
				document.getElementById(enroll.id + "marks").value
			);
		});
	};

	const setStudentMarksHandler = (enrollment, value) => {
		const headers = {
			"Content-Type": "application/json",
		};
		axios
			.post(
				`${baseApiURL()}/marks/addMarks`,
				{
					enrollmentNo: enrollment,
					[selected.examType]: {
						[selected.subject]: value,
					},
				},
				{ headers }
			)
			.then((response) => {
				if (response.data.success) {
					toast.dismiss();
					toast.success(response.data.message);
				} else {
					toast.dismiss();
					toast.error(response.data.message);
				}
			})
			.catch((error) => {
				console.error(error);
				toast.error(error.message);
			});
	};

	// const getBranchData = () => {
	// 	const headers = {
	// 		"Content-Type": "application/json",
	// 	};
	// 	axios
	// 		.get(`${baseApiURL()}/branch/getBranch`, { headers })
	// 		.then((response) => {
	// 			if (response.data.success) {
	// 				setBranch(response.data.branches);
	// 			} else {
	// 				toast.error(response.data.message);
	// 			}
	// 		})
	// 		.catch((error) => {
	// 			console.error(error);
	// 			toast.error(error.message);
	// 		});
	// };

	const getSectionData = () => {
		const headers = {
			"Content-Type": "application/json",
		};
		axios
			.get(`${baseApiURL()}/section/getSection`, { headers })
			.then((response) => {
				if (response.data.success) {
					setSection(response.data.sections);
				} else {
					toast.error(response.data.message);
				}
			})
			.catch((error) => {
				console.error(error);
			});
	};

	const getSubjectData = () => {
		toast.loading("Loading Subjects");
		axios
			.get(`${baseApiURL()}/subject/getSubject`)
			.then((response) => {
				toast.dismiss();
				if (response.data.success) {
					setSubject(response.data.subject);
				} else {
					toast.error(response.data.message);
				}
			})
			.catch((error) => {
				toast.dismiss();
				toast.error(error.message);
			});
	};

	useEffect(() => {
		// getBranchData();
		getSectionData();
		getSubjectData();
	}, []);

	const resetValueHandler = () => {
		setStudentData();
	};

	return (
		<div className='w-full mx-auto flex justify-center items-start flex-col my-10'>
			<div className='relative flex justify-between items-center w-full'>
				<Heading title={`Upload Marks`} />
				{studentData && (
					<button
						className='absolute right-2 flex justify-center items-center border-2 border-red-500 px-3 py-2 rounded text-red-500'
						onClick={resetValueHandler}
					>
						<span className='mr-2'>
							<BiArrowBack className='text-red-500' />
						</span>
						Close
					</button>
				)}
			</div>
			{!studentData && (
				<>
					<div className='mt-10 w-full flex justify-evenly items-center gap-x-6'>
						{/* <div className='w-full'>
							<label
								htmlFor='branch'
								className='leading-7 text-base '
							>
								Select Branch
							</label>
							<select
								id='branch'
								className='px-2 bg-blue-50 py-3 rounded-sm text-base w-full accent-blue-700 mt-1'
								value={selected.branch}
								onChange={(e) =>
									setSelected({ ...selected, branch: e.target.value })
								}
							>
								<option defaultValue>-- Select --</option>
								{branch &&
									branch.map((branch) => {
										return (
											<option
												value={branch.name}
												key={branch.name}
											>
												{branch.name}
											</option>
										);
									})}
							</select>
						</div> */}
						<div className='w-full'>
							<label
								htmlFor='section'
								className='leading-7 text-base '
							>
								Select Section
							</label>
							<select
								id='section'
								className='px-2 bg-blue-50 py-3 rounded-sm text-base w-full accent-blue-700 mt-1'
								value={selected.section}
								onChange={(e) =>
									setSelected({ ...selected, section: e.target.value })
								}
							>
								<option defaultValue>-- Select --</option>
								{section &&
									section.map((section) => {
										return (
											<option
												value={section.name}
												key={section.name}
											>
												{section.name}
											</option>
										);
									})}
							</select>
						</div>
						<div className='w-full'>
							<label
								htmlFor='semester'
								className='leading-7 text-base '
							>
								Select Class
							</label>
							<select
								id='semester'
								className='px-2 bg-blue-50 py-3 rounded-sm text-base w-full accent-blue-700 mt-1'
								value={selected.semester}
								onChange={(e) =>
									setSelected({ ...selected, semester: e.target.value })
								}
							>
								<option defaultValue>-- Select Class --</option>
								<option value='11'>11th</option>
								<option value='12'>12th </option>
							</select>
						</div>
						<div className='w-full'>
							<label
								htmlFor='subject'
								className='leading-7 text-base '
							>
								Select Subject
							</label>
							<select
								id='subject'
								className='px-2 bg-blue-50 py-3 rounded-sm text-base w-full accent-blue-700 mt-1'
								value={selected.subject}
								onChange={(e) =>
									setSelected({ ...selected, subject: e.target.value })
								}
							>
								<option defaultValue>-- Select --</option>
								{subject &&
									subject.map((subject) => {
										return (
											<option
												value={subject.name}
												key={subject.name}
											>
												{subject.name}
											</option>
										);
									})}
							</select>
						</div>
						<div className='w-full'>
							<label
								htmlFor='examType'
								className='leading-7 text-base '
							>
								Select Exam Type
							</label>
							<select
								id='examType'
								className='px-2 bg-blue-50 py-3 rounded-sm text-base w-full accent-blue-700 mt-1'
								value={selected.examType}
								onChange={(e) =>
									setSelected({ ...selected, examType: e.target.value })
								}
							>
								<option defaultValue>-- Select --</option>
								<option value='internal'>Internal</option>
								<option value='external'>External</option>
							</select>
						</div>
					</div>

					<button
						className='bg-blue-50 px-4 py-2 mt-8 mx-auto rounded border-2 border-blue-500 text-black'
						onClick={loadStudentDetails}
					>
						Load Student Data
					</button>
				</>
			)}
			{studentData && studentData.length !== 0 && (
				<>
					<p className='mt-4 text-lg'>
						Upload {selected.examType} Marks Of {selected.subject}
					</p>
					<div
						className='w-full flex flex-wrap justify-center items-center mt-8 gap-4'
						id='markContainer'
					>
						{studentData.map((student) => {
							return (
								<div
									key={student.enrollmentNo}
									className='w-[30%] flex justify-between items-center border-2 border-blue-500 rounded'
									id={student.enrollmentNo}
								>
									<p className='text-lg px-4 w-1/2 bg-blue-50'>
										{student.enrollmentNo}
									</p>
									<input
										type='number'
										className='px-6 py-2 focus:ring-0 outline-none w-1/2'
										placeholder='Enter Marks'
										id={`${student.enrollmentNo}marks`}
									/>
								</div>
							);
						})}
					</div>
					<button
						className='bg-blue-500 px-6 py-3 mt-8 mx-auto rounded text-white'
						onClick={submitMarksHandler}
					>
						Upload Student Marks
					</button>
				</>
			)}
		</div>
	);
};

export default Marks;
