/** @format */

import axios from "axios";
import React, { useEffect, useState } from "react";
import { FiUpload } from "react-icons/fi";
import Heading from "../../components/Heading";
import { AiOutlineClose } from "react-icons/ai";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import toast from "react-hot-toast";
import { storage } from "../../firebase/config";
import { baseApiURL } from "../../baseUrl";
const Timetable = () => {
	const [addselected, setAddSelected] = useState({
		semester: "",
		section: "",
		link: "",
	});
	const [file, setFile] = useState();
	// const [branch, setBranch] = useState();
	const [section, setSection] = useState();

	useEffect(() => {
		// getBranchData();
		getSectionData();
	}, []);

	useEffect(() => {
		const uploadFileToStorage = async (file) => {
			toast.loading("Upload Timetable To Server");
			const storageRef = ref(
				storage,
				`Timetable/${addselected.semester}Semester/ ${addselected.section}Section `
			);
			const uploadTask = uploadBytesResumable(storageRef, file);
			uploadTask.on(
				"state_changed",
				(snapshot) => {},
				(error) => {
					console.error(error);
					toast.dismiss();
					console.log("FIle Upload error", error);
					// toast.error("Something Went Wrong!");
				},
				() => {
					getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
						toast.dismiss();
						setFile();
						toast.success("Timetable Uploaded To Server");
						setAddSelected({ ...addselected, link: downloadURL });
					});
				}
			);
		};
		file && uploadFileToStorage(file);
	}, [file, addselected]);

	// const getBranchData = () => {
	// 	const headers = {
	// 		"Content-Type": "application/json",
	// 	};
	// 	axios
	// 		.get(`${baseApiURL()}/branch/getBranch`, { headers })
	// 		.then((response) => {
	// 			if (response.data.success) {
	// 				setBranch(response.data.branches);
	// 			} else {
	// 				toast.error(response.data.message);
	// 			}
	// 		})
	// 		.catch((error) => {
	// 			console.error(error);
	// 			toast.error(error.message);
	// 		});
	// };

	const getSectionData = () => {
		const headers = {
			"Content-Type": "application/json",
		};
		axios
			.get(`${baseApiURL()}/section/getSection`, { headers })
			.then((response) => {
				if (response.data.success) {
					setSection(response.data.sections);
				} else {
					toast.error(response.data.message);
				}
			})
			.catch((error) => {
				console.error(error);
			});
	};

	const addTimetableHandler = () => {
		toast.loading("Adding Timetable");
		const headers = {
			"Content-Type": "application/json",
		};
		axios
			.post(`${baseApiURL()}/timetable/addTimetable`, addselected, {
				headers: headers,
			})
			.then((response) => {
				toast.dismiss();
				if (response.data.success) {
					toast.success(response.data.message);
					setAddSelected({
						semester: "",
						section: "",
						link: "",
					});
					setFile("");
				} else {
					toast.error(response.data.message);
				}
			})
			.catch((error) => {
				toast.dismiss();
				console.log("FIle error", error);

				toast.error(error.response.data.message);
			});
	};
	return (
		<div className='w-full mx-auto mt-10 flex justify-center items-start flex-col mb-10'>
			<div className='flex justify-between items-center w-full'>
				<Heading title={`Upload Timetable`} />
			</div>
			<div className='w-full flex justify-evenly items-center mt-12'>
				<div className='w-1/2 flex flex-col justify-center items-center'>
					<p className='mb-4 text-xl font-medium'>Add Timetable</p>
					{/* <select
						id='branch'
						className='px-2 bg-blue-50 py-3 rounded-sm text-base w-[80%] accent-blue-700 mt-4'
						value={addselected.branch}
						onChange={(e) =>
							setAddSelected({ ...addselected, branch: e.target.value })
						}
					>
						<option defaultValue>-- Select Branch --</option>
						{branch &&
							branch.map((branch) => {
								return (
									<option
										value={branch.name}
										key={branch.name}
									>
										{branch.name}
									</option>
								);
							})}
					</select> */}

					<select
						onChange={(e) =>
							setAddSelected({ ...addselected, semester: e.target.value })
						}
						value={addselected.semester}
						name='semester'
						id='semester'
						className='px-2 bg-blue-50 py-3 rounded-sm text-base w-[80%] accent-blue-700 mt-4'
					>
						<option defaultValue>-- Select Class --</option>
						<option value='11'>11th</option>
						<option value='12'>12th</option>
					</select>

					<select
						onChange={(e) =>
							setAddSelected({ ...addselected, section: e.target.value })
						}
						value={addselected.section}
						name='section'
						id='section'
						className='px-2 bg-blue-50 py-3 rounded-sm text-base w-[80%] accent-blue-700 mt-4'
					>
						<option defaultValue>-- Select Section--</option>
						{section &&
							section.map((section) => {
								return (
									<option
										value={section.name}
										key={section.name}
									>
										{section.name}
									</option>
								);
							})}
					</select>
					{!addselected.link && (
						<label
							htmlFor='upload'
							className='px-2 bg-blue-50 py-3 rounded-sm text-base w-[80%] mt-4 flex justify-center items-center cursor-pointer'
						>
							Upload Timetable
							<span className='ml-2'>
								<FiUpload />
							</span>
						</label>
					)}
					{addselected.link && (
						<p
							className='px-2 border-2 border-blue-500 py-2 rounded text-base w-[80%] mt-4 flex justify-center items-center cursor-pointer'
							onClick={() => setAddSelected({ ...addselected, link: "" })}
						>
							Remove Selected Timetable
							<span className='ml-2'>
								<AiOutlineClose />
							</span>
						</p>
					)}
					<input
						type='file'
						name='upload'
						id='upload'
						accept='image/*'
						hidden
						onChange={(e) => setFile(e.target.files[0])}
					/>
					<button
						className='bg-blue-500 text-white mt-8 px-4 py-2 rounded-sm'
						onClick={addTimetableHandler}
					>
						Add Timetable
					</button>
				</div>
			</div>
		</div>
	);
};

export default Timetable;
